import React, { Component } from 'react'
import { getClient, createClient } from "../../actions/clientActions";
import PropTypes from "prop-types";
import {connect} from "react-redux";
import classnames from "classnames";



class UpdateClient extends Component {

    constructor(){
        super()

        this.state = {
            "id": "",
            "firstName": "",
            "lastName": "",
            "userName": "",
            "password": "",
            "phone": "",
            "clientIdentifier": "",
            "car": ""
        };
        this.onChange = this.onChange.bind(this);
    }


    componentWillReceiveProps(nexProps){
        const {id,
        firstName,
        lastName,
        userName,
        password,
        phone,
        clientIdentifier,
        car} = nexProps.client;
        this.setState({
            id,
        firstName,
        lastName,
        userName,
        password,
        phone,
        clientIdentifier,
        car
        })
    }


    componentDidMount(){
        const { id } = this.props.match.params;
        this.props.getClient(id, this.props.history);
    }

onChange(e){
    this.setState({[e.target.name]:e.target.value})
}

    render() {
        return    (  
            <div>
            <div className="project">
            <div className="container">
                <div className="row">
                    <div className="col-md-8 m-auto">
                        <h5 className="display-4 text-center">Update  Client form</h5>
                        <hr />
                        <form>
                            <div className="form-group">
                                <input type="text" class="form-control form-control-lg " placeholder="Client First Name" name="firstName" value={this.state.firstName}/>
                            
                            </div>

                            <div className="form-group">
                            <input type="text" class="form-control form-control-lg " placeholder="Client Last Name" name="lastName" value={this.state.lastName}/>
                           
                            </div>

                            <div className="form-group">
                                <input type="text" className="form-control form-control-lg" placeholder="Unique Client ID" name="clientIdentifier" value={this.state.clientIdentifier}
                                
                                    />
                                   
                                    
                            </div>
                            <div className="form-group">
                            <input type="text" class="form-control form-control-lg " placeholder="Phone Number" name="phone" value={this.state.phone} />
                           
                        </div>
                          
                        
                            <h6>User Name</h6>
                            <div className="form-group">
                            <input type="text" class="form-control form-control-lg " placeholder="UserName" name="userName" value={this.state.userName}/>
                           
                            </div>
                            <h6>Password</h6>
                            <div className="form-group">
                            <input type="password" class="form-control form-control-lg " placeholder="Password" name="password"  value={this.state.password}/>
                            
                           
                            </div>
    
                            <input type="submit" className="btn btn-primary btn-block mt-4" />
                        </form>
                    </div>
                </div>
            </div>
        </div>

        </div>)
          
         
        
    }
}

UpdateClient.propTypes = {
    getClient: PropTypes.func.isRequired,
    createClient: PropTypes.func.isRequired,
    client: PropTypes.isRequired
};

const mapStateToProps = state => ({
    client:state.client.client
});


export default connect(mapStateToProps, { getClient, createClient })(UpdateClient);
