import React, { Component } from 'react';
import PropTypes from "prop-types";
import {connect} from "react-redux";
import {createClient} from "../../actions/clientActions";

 class AddClient extends Component {
     constructor(){
         super();

         this.state={
            firstName: "",
            lastName: "",
            userName: "",
            password: "",
            phone: "",
            clientIdentifier: "",
            errors:{}
         };
         this.onChange = this.onChange.bind(this);
         this.onSubmit = this.onSubmit.bind(this);

     }

     componentWillReceiveProps(nextProps){
         if(nextProps.errors){
             this.setState({ errors:newProps.errors })
         }

     }




     onChange(e){
        this.setState({[e.target.name]:e.target.value })
     }

     onSubmit(e){
         e.preventDefault();
         const newClient ={
            firstName: this.state.firstName,
            lastName: this.state.lastName,
            userName: this.state.userName,
            password: this.state.password,
            phone: this.state.phone,
            clientIdentifier: this.state.clientIdentifier

         };

         this.props.createClient(newClient, this.props.history)
     }



    render() {
        return (
            <div className="project">
            <div className="container">
                <div className="row">
                    <div className="col-md-8 m-auto">
                        <h5 className="display-4 text-center">Create  Client form</h5>
                        <hr />
                        <form onSubmit={this.onSubmit}>
                            <div className="form-group">
                                <input type="text" className="form-control form-control-lg " placeholder="Client Name" name="firstName" value={this.state.firstName}
                                onChange={this.onChange}/>
                            </div>
                            <div className="form-group">
                                <input type="text" className="form-control form-control-lg" placeholder="Unique Client ID" name="clientIdentifier" value={this.state.clientIdentifier}
                                onChange={this.onChange}
                                    />
                            </div>
                            <div className="form-group">
                            <input type="text" className="form-control form-control-lg " placeholder="Phone Number" name="phone" value={this.state.phone}  onChange={this.onChange}/>
                        </div>
                          
                            <div className="form-group">
                            <input type="text" className="form-control form-control-lg " placeholder="Last Name" name="lastName" value={this.state.lastName}  onChange={this.onChange}/>
                            </div>
                            <h6>User Name</h6>
                            <div className="form-group">
                            <input type="text" className="form-control form-control-lg " placeholder="UserName" name="userName" value={this.state.userName}  onChange={this.onChange}/>
                            </div>
                            <h6>Password</h6>
                            <div className="form-group">
                            <input type="password" className="form-control form-control-lg " placeholder="Password" name="password" value={this.state.password}  onChange={this.onChange}/>
                           
                            </div>
    
                            <input type="submit" className="btn btn-primary btn-block mt-4" />
                        </form>
                    </div>
                </div>
            </div>
        </div>
        )
    }
}

AddClient.propTypes = {
    createClient : PropTypes.func.isRequired,
    errors: PropTypes.object.isRequired
};

const mapStateToProps = state =>({
    errors: state.errors 
})

export default connect(
    null,
     { createClient }
     )(AddClient);
